// visualizations.js

const distritosCrData = {
    "name": "Costa Rica",
    "children": [
        { "name": "San José", "children": [/* ... */] },
        { "name": "Alajuela", "children": [/* ... */] },
        { "name": "Cartago", "children": [/* ... */] },
        { "name": "Heredia", "children": [/* ... */] },
        { "name": "Guanacaste", "children": [/* ... */] },
        { "name": "Puntarenas", "children": [/* ... */] },
        { "name": "Limón", "children": [/* ... */] }
    ]
};

const flareData = {
    "name": "flare",
    "children": [/* ... */]
};

const vueData = {
    "name": "vue",
    "children": [/* ... */]
};

function generateUID() {
    return 'uid-' + Math.random().toString(36).substr(2, 9);
}

// Function to draw a Treemap
function drawTreemap(data, elementId) {
    console.log("Drawing Treemap for:", elementId, data);

    const width = 800;
    const height = 600;

    const root = d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.height - a.height || b.value - a.value);

    d3.treemap()
        .size([width, height])
        .padding(2)
        .round(true)(root);

    const svg = d3.select(elementId).append("svg")
        .attr("width", width)
        .attr("height", height)
        .style("font", "12px sans-serif");

    console.log("SVG element created for Treemap:", elementId);

    const cell = svg.selectAll("g")
        .data(root.leaves())
        .join("g")
        .attr("transform", d => `translate(${d.x0},${d.y0})`);

    cell.append("rect")
        .attr("id", d => generateUID())
        .attr("fill", d => d3.interpolateViridis(d.value / root.value))
        .attr("stroke", "#fff")
        .attr("stroke-width", 1)
        .attr("width", d => d.x1 - d.x0)
        .attr("height", d => d.y1 - d.y0);

    cell.append("clipPath")
        .attr("id", d => generateUID())
        .append("use")
        .attr("xlink:href", d => '#' + d.leafUid);

    cell.append("text")
        .attr("clip-path", d => `url(#${d.clipUid})`)
        .selectAll("tspan")
        .data(d => d.data.name.split(/(?=[A-Z][^A-Z])/g))
        .join("tspan")
        .attr("x", 3)
        .attr("y", (d, i) => `${1.1 + i * 1.1}em`)
        .text(d => d)
        .attr("fill", "white");

    console.log("Treemap drawn for:", elementId);
}

// Function to draw a Radial layout
function drawRadial(data, elementId) {
    console.log("Drawing Radial for:", elementId, data);

    const width = 800;
    const height = 600;
    const radius = (Math.min(width, height) / 2) - 10;

    const root = d3.hierarchy(data)
        .sum(d => d.size);

    const tree = d3.tree()
        .size([2 * Math.PI, radius]);

    tree(root);

    const svg = d3.select(elementId).append("svg")
        .attr("width", width)
        .attr("height", height)
        .append("g")
        .attr("transform", `translate(${width / 2},${height / 2})`);

    console.log("SVG element created for Radial:", elementId);

    const link = svg.append("g")
        .selectAll("path")
        .data(root.links())
        .join("path")
        .attr("fill", "none")
        .attr("stroke", "#ccc")
        .attr("stroke-opacity", 0.8)
        .attr("stroke-width", 1.5)
        .attr("d", d3.linkRadial()
            .angle(d => d.x)
            .radius(d => d.y));

    const node = svg.append("g")
        .selectAll("g")
        .data(root.descendants())
        .join("g")
        .attr("transform", d => `
            rotate(${d.x * 180 / Math.PI - 90})
            translate(${d.y},0)
        `);

    node.append("circle")
        .attr("fill", d => d.children ? "#555" : "#999")
        .attr("r", 4);

    node.append("text")
        .attr("dy", "0.31em")
        .attr("x", d => d.x < Math.PI === !d.children ? 6 : -6)
        .attr("text-anchor", d => d.x < Math.PI === !d.children ? "start" : "end")
        .attr("transform", d => d.x >= Math.PI ? "rotate(180)" : null)
        .text(d => d.data.name)
        .attr("fill", "#333");

    console.log("Radial drawn for:", elementId);
}

// Function to draw a Partition layout
function drawPartition(data, elementId) {
    console.log("Drawing Partition for:", elementId, data);

    const width = 800;
    const height = 600;

    const root = d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.height - a.height || b.value - a.value);

    d3.partition()
        .size([height, width])
        .padding(2)
        .round(true)(root);

    const svg = d3.select(elementId).append("svg")
        .attr("width", width)
        .attr("height", height)
        .style("font", "12px sans-serif");

    console.log("SVG element created for Partition:", elementId);

    const cell = svg.selectAll("g")
        .data(root.descendants())
        .join("g")
        .attr("transform", d => `translate(${d.y0},${d.x0})`);

    cell.append("rect")
        .attr("width", d => d.y1 - d.y0)
        .attr("height", d => d.x1 - d.x0)
        .attr("fill", d => d3.interpolateViridis(d.value / root.value))
        .attr("stroke", "#fff")
        .attr("stroke-width", 1);

    cell.append("text")
        .attr("x", 4)
        .attr("y", 13)
        .text(d => d.data.name)
        .attr("fill", "white");

    console.log("Partition drawn for:", elementId);
}

// Function to draw a Circle Packing layout
function drawCirclePacking(data, elementId) {
    console.log("Drawing Circle Packing for:", elementId, data);

    const width = 800;
    const height = 800;

    const root = d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.value - a.value);

    d3.pack()
        .size([width, height])
        .padding(2)(root);

    const svg = d3.select(elementId).append("svg")
        .attr("width", width)
        .attr("height", height)
        .style("font", "12px sans-serif");

    console.log("SVG element created for Circle Packing:", elementId);

    const node = svg.selectAll("g")
        .data(root.descendants())
        .join("g")
        .attr("transform", d => `translate(${d.x},${d.y})`);

    node.append("circle")
        .attr("r", d => d.r)
        .attr("fill", d => d.children ? d3.interpolateViridis(d.value / root.value) : "#fff")
        .attr("stroke", "#999")
        .attr("stroke-width", 1);

    node.append("text")
        .attr("dy", "0.3em")
        .attr("text-anchor", "middle")
        .text(d => d.children ? "" : d.data.name)
        .attr("fill", "#333");

    console.log("Circle Packing drawn for:", elementId);
}

// Draw visualizations directly using the data variables
console.log("Starting visualization drawing");

drawTreemap(distritosCrData, '#treemap-distritos_cr');
drawRadial(distritosCrData, '#radial-distritos_cr');
drawPartition(distritosCrData, '#partition-distritos_cr');
drawCirclePacking(distritosCrData, '#circle-packing-distritos_cr');

drawTreemap(flareData, '#treemap-flare');
drawRadial(flareData, '#radial-flare');
drawPartition(flareData, '#partition-flare');
drawCirclePacking(flareData, '#circle-packing-flare');

drawTreemap(vueData, '#treemap-vue');
drawRadial(vueData, '#radial-vue');
drawPartition(vueData, '#partition-vue');
drawCirclePacking(vueData, '#circle-packing-vue');
