d3.json("../data/flare.json").then(data => {
    console.log("Radial Data:", data);
    const width = 1000;
    const height = 500;
    const radius = Math.min(width, height) / 2;

    const root = d3.hierarchy(data)
        .sum(d => d.value);

    d3.partition()
        .size([2 * Math.PI, radius])(root);

    console.log("Radial Root:", root);

    const svg = d3.select("#radial svg")
        .attr("viewBox", [-width / 2, -height / 2, width, height]);

    const arc = d3.arc()
        .startAngle(d => d.x0)
        .endAngle(d => d.x1)
        .innerRadius(d => d.y0)
        .outerRadius(d => d.y1);

    const color = d3.scaleOrdinal(d3.quantize(d3.interpolateRainbow, root.children.length + 1));

    svg.selectAll("path")
        .data(root.descendants())
        .enter().append("path")
        .attr("d", arc)
        .attr("fill", d => color(d.depth))
        .append("title")
        .text(d => `${d.ancestors().map(d => d.data.name).reverse().join("/")}\n${d.value}`);
}).catch(error => console.error("Error loading Radial Data:", error));
