import * as d3 from 'd3';

d3.json("../data/distritos_cr.json").then(data => {
    console.log("Circle Packing Data:", data);
    const width = 1000;
    const height = 500;
    const pack = d3.pack()
        .size([width, height])
        .padding(1.5);

    const root = d3.hierarchy(data)
        .sum(d => d.size)
        .sort((a, b) => b.value - a.value);

    console.log("Circle Packing Root:", root);

    const nodes = pack(root).descendants();

    const svg = d3.select("#circlePacking svg");

    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const node = svg.selectAll("g")
        .data(nodes)
        .enter().append("g")
        .attr("transform", d => `translate(${d.x},${d.y})`);

    node.append("circle")
        .attr("r", d => d.r)
        .attr("fill", d => color(d.depth));

    node.append("text")
        .attr("dy", "0.3em")
        .text(d => d.data.name)
        .attr("text-anchor", "middle");
}).catch(error => console.error("Error loading Circle Packing Data:", error));
