import * as d3 from 'd3';

d3.json("/data/flare.json").then(data => {
    console.log("Treemap Data:", data);
    const width = 1000;
    const height = 500;

    const root = d3.hierarchy(data)
      .sum(d => d.value)
      .sort((a, b) => b.value - a.value);

    d3.treemap()
      .size([width, height])
      .padding(1)
      .round(true)(root);

    console.log("Treemap Root:", root);

    const svg = d3.select("#treemap svg");

    const color = d3.scaleSequential([0, root.height], d3.interpolateBlues);

    svg.selectAll("rect")
      .data(root.leaves())
      .enter().append("rect")
      .attr("x", d => d.x0)
      .attr("y", d => d.y0)
      .attr("width", d => d.x1 - d.x0)
      .attr("height", d => d.y1 - d.y0)
      .attr("fill", d => color(d.depth));

    svg.selectAll("text")
      .data(root.leaves())
      .enter().append("text")
      .attr("x", d => d.x0 + 5)
      .attr("y", d => d.y0 + 20)
      .text(d => d.data.name);
  }).catch(error => console.error("Error loading Treemap Data:", error));
