import * as d3 from 'd3';

d3.json("../data/vue.json").then(data => {
    console.log("Partition Data:", data);
    const width = 1000;
    const height = 500;

    const root = d3.hierarchy(data)
        .sum(d => d.size);

    d3.partition()
        .size([width, height])
        .padding(1)
        .round(true)(root);

    console.log("Partition Root:", root);

    const svg = d3.select("#partition svg");

    const color = d3.scaleOrdinal(d3.quantize(d3.interpolateRainbow, root.children.length + 1));

    svg.selectAll("rect")
        .data(root.descendants())
        .enter().append("rect")
        .attr("x", d => d.x0)
        .attr("y", d => d.y0)
        .attr("width", d => d.x1 - d.x0)
        .attr("height", d => d.y1 - d.y0)
        .attr("fill", d => color(d.depth));

    svg.selectAll("text")
        .data(root.descendants())
        .enter().append("text")
        .attr("x", d => d.x0 + 5)
        .attr("y", d => d.y0 + 20)
        .text(d => d.data.name);
}).catch(error => console.error("Error loading Partition Data:", error));
